import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 2, description: "Charger", quantity: 1, packed: true },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <ParkingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>üå¥Far Awayüéí</h1>;
}

function Form() {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [item, setItem] = useState([]);

  function handleAddItem(item) {}
  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return;
    // using handlesubmit function for detailing

    const newItems = { description, quantity, parked: false, id: Date.now() };
    console.log(newItems);

    handleAddItem(newItems);

    setDescription("");
    setQuantity("");
  }

  // function handleclick(e) {
  //   e.previentDefualt();
  // }
  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòä trip ?</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option key={num}>{num}</option>
        ))}
      </select>

      {/* <option value={1}>1</option>
        <option value={2}>2</option>
        <option value={3}>3</option> */}

      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button>Add</button>
    </form>
  );
}

function ParkingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => {
          <Item item={item} />;
        })}
      </ul>
    </div>
  );
}
function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.description}
        {item.quantity}
      </span>
      <button>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <footer className="stats">
      You have X items in your list and you already pack x (X%)
    </footer>
  );
}
